    private void jButton_ingresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_ingresarActionPerformed
        // TODO add your handling code here:
        String usuario = jTextField_usuario.getText();
        String clave = String.valueOf(jPasswordField_clave.getPassword());
        if(usuario.isEmpty() || clave.isEmpty()){
            JOptionPane.showMessageDialog(null, "Los campos no tienen que estar vac√≠os.");
        }else{
            //validar usuarios
            GestorBD gestor = new GestorBD();
            int acceso = gestor.validarAcceso(usuario, clave);
            switch (acceso) {
                case -2: case -1:
                    JOptionPane.showMessageDialog(null, "No es posible el acceso, cod(" + acceso + ")");
                    break;
                case 0:
                    JOptionPane.showMessageDialog(null, "Credenciales Incorrectas");
                    break;
                case 1:
                    new Ventana().setVisible(true);
                    this.dispose();
                    break;
                default:
                    throw new AssertionError();
            }
        }
    }//GEN-LAST:event_jButton_ingresarActionPerformed

